import React,{ useState } from "react";
import "./Projects.css";
import "./l+f.png";

export default function Projects() {
    const [isHovered, setIsHovered] = useState(false);

    const handleMouseEnter = () => {
      setIsHovered(true);
    };

    const handleMouseLeave = () => {
      setIsHovered(false);
    };

    return (
        <main>
            <div className="container">
                <h1>My Projects</h1>    
                <hr className="long"></hr>
                <h2>Video Games</h2>    
                <hr className="divider"></hr>
                <div class="grille">
                <div className={`grid-item ${isHovered ? "hover" : ""}`} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                    <img src="https://img.itch.zone/aW1nLzc5NjE2NDkucG5n/original/npFs7v.png"/>
                    <p>Nyasteroid</p>
                        84th Mini Jam. I worked as composer. 
                        <a href="https://img.itch.zone/aW1nLzc5NjE2NDkucG5n/original/npFs7v.png" className="button">zijlij</a>
                    </div>
                    <div class="grid-item"><img src="https://img.itch.zone/aW1nLzc5NjE2NDkucG5n/original/npFs7v.png"/>
                    <p>Live and Evil</p>
                        2022's Coding Blocks Jam. I worked as composer. 
                    </div>                   
	            </div>
            </div>
        </main>
    );
}
